class HashTable {
  constructor(size = 53) {
    this.keyMap = new Array(size);
  }

  // í•´ì‹œ í•¨ìˆ˜
  _hash(key) {
    let total = 0;
    const PRIME = 31;

    for (let i = 0; i < Math.min(key.length, 100); i++) {
      const char = key[i];
      const value = char.charCodeAt(0) - 96;
      total = (total * PRIME + value) % this.keyMap.length;
    }

    return total;
  }

  // ì‚½ì…: O(1) í‰ê· 
  set(key, value) {
    const index = this._hash(key);

    if (!this.keyMap[index]) {
      this.keyMap[index] = [];
    }

    // ì¶©ëŒ ì²˜ë¦¬: Separate Chaining
    const bucket = this.keyMap[index];
    for (let i = 0; i < bucket.length; i++) {
      if (bucket[i][0] === key) {
        bucket[i][1] = value;
        return;
      }
    }

    bucket.push([key, value]);
  }

  // ì¡°íšŒ: O(1) í‰ê· 
  get(key) {
    const index = this._hash(key);
    const bucket = this.keyMap[index];

    if (bucket) {
      for (const [k, v] of bucket) {
        if (k === key) return v;
      }
    }

    return undefined;
  }

  // ì‚­ì œ: O(1) í‰ê· 
  delete(key) {
    const index = this._hash(key);
    const bucket = this.keyMap[index];

    if (bucket) {
      for (let i = 0; i < bucket.length; i++) {
        if (bucket[i][0] === key) {
          bucket.splice(i, 1);
          return true;
        }
      }
    }

    return false;
  }

  // ëª¨ë“  í‚¤
  keys() {
    const keys = [];
    for (const bucket of this.keyMap) {
      if (bucket) {
        for (const [key] of bucket) {
          keys.push(key);
        }
      }
    }
    return keys;
  }

  // ëª¨ë“  ê°’
  values() {
    const values = [];
    const seen = new Set();

    for (const bucket of this.keyMap) {
      if (bucket) {
        for (const [, value] of bucket) {
          if (!seen.has(value)) {
            values.push(value);
            seen.add(value);
          }
        }
      }
    }

    return values;
  }
}
